import { Button, IconButton, Modal, Typography } from "@mui/material";
import Box from "@mui/material/Box";
import FullscreenIcon from "@mui/icons-material/Fullscreen";
import { width } from "@mui/system";
import React from "react";

import { trafficDate } from "./charts/Barcharts";

import { EntryExit } from "../services/entryExit";

type Props = {
    /// <reference path="" />
    children?: React.ReactElement;
    fullScreen?: boolean;
    chart?: React.FC;
    data?:  trafficDate  | EntryExit[];
};

const Card = ({ children, fullScreen = true, chart, data }: Props) => {
    const [open, setOpen] = React.useState(false);
    return (
        <Box
            className="h-full rounded-md"
            sx={{
                backgroundColor: "background.paper",
            }}
        >
            {fullScreen && (
                <IconButton
                    sx={{
                        position: "relative",
                        top: "0",
                        left: "0",
                    }}
                    onClick={() => setOpen(true)}
                    size="small"
                >
                    <FullscreenIcon />
                </IconButton>
            )}
            <Modal open={open}>
                <Box
                    className="flex flex-col justify-between items-center p-4 rounded-md filter drop-shadow-xl"
                    sx={{
                        position: "absolute",
                        top: "50%",
                        left: "50%",
                        width: "70vw",
                        height: "70vh",
                        transform: "translate(-50%, -50%)",
                        backgroundColor: "background.paper",
                        opacity: 0.9,
                    }}
                >
                    <>
                        {chart &&
                            chart({ width: 900, height: 400, data: data })}
                        <Button
                            variant="contained"
                            onClick={() => setOpen(false)}
                            size="small"
                        >
                            <Typography variant="body2">Close</Typography>
                        </Button>
                    </>
                </Box>
            </Modal>
            <Box
                id="chartContainer"
                className="flex flex-col justify-center items-center relative bottom-3"
            >
                {chart && chart({ width: 300, height: 250, data: data })}
            </Box>
            {children && children}
        </Box>
    );
};

export default Card;
